
<div class="zoomAnim alert-form">
	<h4 class="other-title">Пожаловаться на объявление</h4>
	<div data-form="true" class="wForm wFormDef wFormPreloader">
		<div class="wFormRow">
			<label for="demo_email_input" class="wLabel -warn">Ваш Email</label>
			<div class="wFormInput">
				<input class="wInput" required="required" type="email" name="demo_email_input" id="demo_email_input" placeholder="" data-rule-email="true"/>
				<div class="inpInfo">Ваш Email</div>
			</div>
		</div>
		<div class="wFormRow">
			<label for="demo_maxlength_textarea32" class="wLabel -warn">Текст сообщения</label>
			<div class="wFormInput">
				<textarea class="wTextarea" required="required" name="demo_maxlength_textarea32" id="demo_maxlength_textarea32" placeholder="" data-rule-minlength='20'></textarea>
				<div class="inpInfo">Текст сообщения</div>
			</div>
		</div>
		<div class="wFormRow clearfix">
			<div class="wSubmit green-button green-button--small w_flr">Отправить</div>
		</div>
	</div>
</div>